import { computed } from "vue";
import { useRoute } from "vue-router";
import AppSidebar from "@/components/AppSidebar.vue"; // Pastikan path benar

export default {
  __name: 'App',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const route = useRoute();

    // Komputer untuk menentukan apakah sidebar harus ditampilkan
    const showSidebar = computed(() => {
      // Ambil nama rute saat ini dan cek apakah itu 'Login'
      // atau cek menggunakan meta tag 'public'
      return route.name !== "Login";
      // ATAU: return !route.meta.public; // Jika Anda suka menggunakan meta tag
    });

    // Komputer untuk mengatur kelas pada <main> agar terpusat/ber-padding dengan benar
    const mainClass = computed(() => {
      if (showSidebar.value) {
        // Jika ada sidebar, gunakan padding kiri untuk mengimbangi sidebar (72 = 18rem)
        return "flex-1 p-8 pl-72";
      } else {
        // Jika tidak ada sidebar (halaman login), biarkan flex-1 dan p-8 biasa
        return "flex-1 p-0"; // Hapus padding default di sini, biarkan form login sendiri yang mengatur centering
      }
    });
    const __returned__ = {
      route,
      showSidebar,
      mainClass,
      computed,
      get useRoute() {
        return useRoute;
      },
      AppSidebar
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
