import "core-js/modules/es.iterator.constructor.js";
import "core-js/modules/es.iterator.filter.js";
import "core-js/modules/es.iterator.map.js";
import "core-js/modules/es.iterator.some.js";
import { ref, onMounted, computed } from "vue";
import axios from "axios";
import GudangLayouts from "@/components/GudangLayouts.vue";

// --------------------------------------------------------
// 1. STATE MANAGEMENT
// --------------------------------------------------------

// Data Laporan Utama

export default {
  __name: 'PurchaseReturn',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const purchaseReturns = ref([]);
    const loadingReturns = ref(false);
    const returnFromDate = ref("");
    const returnToDate = ref("");
    const returnStatusFilter = ref("");

    // Data Modal Pilih PO
    const purchasesHistory = ref([]);
    const loadingHistory = ref(false);
    const showSelectPOModal = ref(false);
    const fromDate = ref("");
    const toDate = ref("");

    // Data Modal Form Retur
    const showModal = ref(false);
    const selectedPurchase = ref(null);
    const returnForm = ref({
      items: [],
      notes: ""
    });
    const submitting = ref(false);

    // --------------------------------------------------------
    // 2. COMPUTED PROPERTIES (VALIDASI & LOGIKA TAMPILAN)
    // --------------------------------------------------------

    const hasValidItems = computed(() => {
      return returnForm.value.items.some(item => item.qty > 0 && item.reason.trim().length > 3 // Menambah validasi alasan minimal 4 karakter
      );
    });
    const validReturnItemsCount = computed(() => {
      return returnForm.value.items.filter(item => item.qty > 0 && item.reason.trim().length > 3).length;
    });

    // --------------------------------------------------------
    // 3. PENGATURAN API
    // --------------------------------------------------------

    const api = axios.create({
      baseURL: "http://localhost:8081/api/v1"
    });
    const savedToken = localStorage.getItem("token");
    if (savedToken) {
      api.defaults.headers.common["Authorization"] = `Bearer ${savedToken}`;
    }

    // --------------------------------------------------------
    // 4. FUNGSI UNTUK DAFTAR RETUR UTAMA
    // --------------------------------------------------------

    const fetchPurchaseReturns = async () => {
      loadingReturns.value = true;
      try {
        const params = {
          page: 1,
          size: 10
        };
        if (returnFromDate.value) params.start_date = returnFromDate.value;
        if (returnToDate.value) params.end_date = returnToDate.value;
        // Jika status kosong, backend diasumsikan default ke PENDING & QC_DEFECT
        if (returnStatusFilter.value) params.status = returnStatusFilter.value;
        const response = await api.get("/history/purchase-returns", {
          params
        });
        const rawData = response.data.data || response.data || [];
        purchaseReturns.value = Array.isArray(rawData) ? rawData.filter(ret => ret && ret.id) : [];
      } catch (error) {
        console.error("Error fetching returns:", error.response?.data || error.message);
        alert(`Gagal mengambil daftar retur: ${error.response?.data?.error || "Cek otorisasi."}`);
        purchaseReturns.value = [];
      } finally {
        loadingReturns.value = false;
      }
    };
    const resetReturnFilter = () => {
      returnFromDate.value = "";
      returnToDate.value = "";
      returnStatusFilter.value = "";
      fetchPurchaseReturns();
    };

    // --------------------------------------------------------
    // 5. FUNGSI UNTUK MODAL PILIH PO
    // --------------------------------------------------------

    const fetchPurchasesHistory = async (status = "RECEIVED") => {
      loadingHistory.value = true;
      try {
        const params = {
          page: 1,
          size: 10,
          status: status // Hanya PO yang sudah RECEIVED
        };
        if (fromDate.value) params.start_date = fromDate.value;
        if (toDate.value) params.end_date = toDate.value;
        const response = await api.get("/history/purchases", {
          params
        });
        const data = response.data;
        let rawData = [];
        if (data && data.data && Array.isArray(data.data.data)) {
          rawData = data.data.data;
        } else if (data && Array.isArray(data.data)) {
          rawData = data.data;
        } else if (data && Array.isArray(data)) {
          rawData = data;
        }
        purchasesHistory.value = rawData.filter(p => p && (p.ID || p.id));
      } catch (error) {
        console.error("Error fetch purchases for manual return:", error);
        alert(`Error memuat PO: ${error.response?.data?.error || "Kesalahan jaringan."}`);
        purchasesHistory.value = [];
      } finally {
        loadingHistory.value = false;
      }
    };
    const openSelectPOModal = () => {
      fetchPurchasesHistory("RECEIVED");
      showSelectPOModal.value = true;
    };
    const closeSelectPOModal = () => {
      showSelectPOModal.value = false;
      purchasesHistory.value = [];
    };
    const resetFilter = () => {
      fromDate.value = "";
      toDate.value = "";
      fetchPurchasesHistory("RECEIVED");
    };

    // --------------------------------------------------------
    // 6. FUNGSI UNTUK MODAL FORM RETUR
    // --------------------------------------------------------

    const openReturnModal = async purchaseId => {
      closeSelectPOModal();
      if (!purchaseId) {
        alert("ID pembelian tidak valid.");
        return;
      }
      try {
        // Ambil detail PO dari endpoint detail. Asumsi: /gudang/purchases/:id
        const response = await api.get(`/gudang/purchases/${purchaseId}`);
        selectedPurchase.value = response.data.data || response.data;
        const items = selectedPurchase.value.Items || [];

        // Inisialisasi return form items
        returnForm.value.items = items.map(item => ({
          purchase_item_id: item.ID || item.id,
          product_id: item.ProductID || item.product_id,
          qty: 0,
          reason: "",
          price: item.Price || item.price
        }));
        returnForm.value.notes = "";
        showModal.value = true;
      } catch (error) {
        console.error("Error fetching purchase:", error);
        alert(`Gagal memuat detail pembelian: ${error.response?.data?.error || error.message}`);
      }
    };
    const closeModal = () => {
      showModal.value = false;
      selectedPurchase.value = null;
      returnForm.value = {
        items: [],
        notes: ""
      };
    };
    const submitReturn = async () => {
      if (!hasValidItems.value) {
        alert("Pilih minimal satu item dengan Qty > 0 dan Alasan yang detail (min. 4 karakter).");
        return;
      }
      submitting.value = true;
      try {
        const validItems = returnForm.value.items.filter(item => item.qty > 0 && item.reason.trim().length > 3).map(item => ({
          product_id: item.product_id,
          purchase_item_id: item.purchase_item_id,
          qty: item.qty,
          price: item.price,
          reason: item.reason
        }));
        const dto = {
          purchase_id: selectedPurchase.value.ID || selectedPurchase.value.id,
          date: new Date().toISOString().split("T")[0],
          items: validItems,
          notes: returnForm.value.notes
        };
        const response = await api.post("/gudang/purchase-returns", dto);
        if (response.status === 201 || response.status === 200) {
          alert("Retur pembelian manual berhasil dibuat (status: PENDING).");
          closeModal();
          fetchPurchaseReturns();
        }
      } catch (error) {
        console.error("Error creating return:", error.response?.data || error);
        alert(`Gagal membuat retur: ${error.response?.data?.error || error.response?.data?.message || "Kesalahan tidak diketahui."}`);
      } finally {
        submitting.value = false;
      }
    };

    // --------------------------------------------------------
    // 7. UTILITY FUNCTIONS (FORMATTING)
    // --------------------------------------------------------

    const formatPONumber = id => `PO-${String(id || "").padStart(3, "0")}`;
    const formatReturnNumber = id => String(id || "").padStart(4, "0");
    const getStatusClass = status => {
      switch (status) {
        case "PENDING":
          return "bg-yellow-100 text-yellow-800 border border-yellow-300";
        case "APPROVED":
          return "bg-green-100 text-green-800 border border-green-300";
        case "REJECTED":
          return "bg-red-100 text-red-800 border border-red-300";
        case "QC_DEFECT":
          return "bg-red-200 text-red-900 border border-red-500 font-bold";
        default:
          return "bg-gray-100 text-gray-800 border border-gray-300";
      }
    };
    const getStatusText = status => {
      switch (status) {
        case "PENDING":
          return "Pending (Retur Manual)";
        case "APPROVED":
          return "Approved";
        case "REJECTED":
          return "Rejected";
        case "QC_DEFECT":
          return "Laporan QC Defect";
        default:
          return status || "Unknown";
      }
    };
    const formatDate = dateStr => {
      if (!dateStr) return "N/A";
      const date = new Date(dateStr);
      if (isNaN(date.getTime())) return dateStr.split("T")[0] || "Invalid Date";
      return date.toLocaleDateString("id-ID", {
        day: "2-digit",
        month: "2-digit",
        year: "numeric"
      });
    };
    const formatCurrency = amount => {
      if (amount === null || amount === undefined) return "Rp 0";
      return new Intl.NumberFormat("id-ID", {
        style: "currency",
        currency: "IDR",
        minimumFractionDigits: 0,
        maximumFractionDigits: 0
      }).format(amount);
    };

    // --------------------------------------------------------
    // 8. LIFECYCLE HOOKS
    // --------------------------------------------------------

    onMounted(() => {
      fetchPurchaseReturns();
    });
    const __returned__ = {
      purchaseReturns,
      loadingReturns,
      returnFromDate,
      returnToDate,
      returnStatusFilter,
      purchasesHistory,
      loadingHistory,
      showSelectPOModal,
      fromDate,
      toDate,
      showModal,
      selectedPurchase,
      returnForm,
      submitting,
      hasValidItems,
      validReturnItemsCount,
      api,
      savedToken,
      fetchPurchaseReturns,
      resetReturnFilter,
      fetchPurchasesHistory,
      openSelectPOModal,
      closeSelectPOModal,
      resetFilter,
      openReturnModal,
      closeModal,
      submitReturn,
      formatPONumber,
      formatReturnNumber,
      getStatusClass,
      getStatusText,
      formatDate,
      formatCurrency,
      ref,
      onMounted,
      computed,
      get axios() {
        return axios;
      },
      GudangLayouts
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
