    }
    const fetchPurchasesHistory = async () => {
      loadingHistory.value = true;
      try {
        const nameFromStorage = localStorage.getItem("userName");
        if (nameFromStorage) {
          userName.value = nameFromStorage;
        }
        const params = {
          page: 1,
          size: 10,
