        if (!selectedPurchase.value) {
          alert("Data pembelian tidak ditemukan.");
          return;
        }
        const items = selectedPurchase.value.Items || [];
        if (!Array.isArray(items)) {
          alert("Items pembelian tidak valid.");
          return;
        }
        returnForm.value.items = items.map(item => ({
