import "core-js/modules/es.iterator.constructor.js";
import "core-js/modules/es.iterator.filter.js";
import { ref, onMounted } from 'vue';
import PurchasingLayouts from '../components/PurchasingLayouts.vue';
import axios from 'axios';

// State untuk menyimpan data dashboard

export default {
  __name: 'DashboardPembelian',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const dashboardData = ref({
      purchase_orders_to_process: 0,
      returns_to_review: 0,
      products_in_stock: 0
    });
    const loading = ref(true);

    // Fungsi untuk mengambil data dari backend
    const fetchDashboardData = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          console.error('Token autentikasi tidak ditemukan. Silakan login kembali.');
          return;
        }
        loading.value = true;

        // Ambil jumlah purchase orders dengan status SUBMITTED
        const purchaseResponse = await axios.get('http://localhost:8081/api/v1/pembelian/purchases', {
          params: {
            status: 'SUBMITTED',
            size: 1
          },
          headers: {
            Authorization: `Bearer ${token}`
          }
        });
        dashboardData.value.purchase_orders_to_process = purchaseResponse.data.total || 0;

        // Ambil jumlah purchase returns dengan status PENDING
        const returnResponse = await axios.get('http://localhost:8081/api/v1/pembelian/purchase-returns', {
          params: {
            status: 'PENDING',
            size: 1
          },
          headers: {
            Authorization: `Bearer ${token}`
          }
        });
        dashboardData.value.returns_to_review = returnResponse.data.total || 0;

        // Ambil jumlah produk dengan stok > 0
        const productResponse = await axios.get('http://localhost:8081/api/v1/products', {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });
        dashboardData.value.products_in_stock = productResponse.data.filter(product => product.stock > 0).length;
      } catch (error) {
        console.error('Gagal mengambil data dashboard:', error);
        if (error.response) {
          if (error.response.status === 401) {
            console.error('Sesi Anda telah berakhir. Silakan login kembali.');
          } else if (error.response.status === 404) {
            console.error('Endpoint tidak ditemukan. Periksa konfigurasi server.');
          } else {
            console.error('Terjadi kesalahan saat memuat data:', error.message);
          }
        } else {
          console.error('Koneksi ke server gagal. Periksa jaringan Anda.');
        }
      } finally {
        loading.value = false;
      }
    };

    // Panggil fungsi saat komponen dimuat
    onMounted(fetchDashboardData);
    const __returned__ = {
      dashboardData,
      loading,
      fetchDashboardData,
      ref,
      onMounted,
      PurchasingLayouts,
      get axios() {
        return axios;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
