    __expose();
    const fetchDashboardData = async () => {
      try {
        // 1. Ambil data stok: Tambahkan parameter ?type=stocks
        const stockResponse = await axios.get("/api/v1/kepala-gudang/reports/stocks",
        // Endpoint tetap sama
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("token")}`
          },
          params: {
            // Tambahkan parameter type yang diharapkan oleh GenerateReport
            type: "stocks"
          }
        });
        // Asumsi data yang dikembalikan adalah array data di response.data.data
        stockData.value = stockResponse.data.data || [];

        // 2. Ambil data riwayat stok: Tambahkan parameter ?type=stock-histories & filter tanggal
        const historyResponse = await axios.get("/api/v1/kepala-gudang/reports/stock-histories",
        // Endpoint tetap sama
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("token")}`
          },
          params: {
            // Tambahkan parameter type yang diharapkan oleh GenerateReport
            type: "stock-histories",
            from: today,
            to: today
          }
        });
        // Asumsi data yang dikembalikan adalah array data di response.data.data
        stockHistoryData.value = historyResponse.data.data || [];
      } catch (error) {
