        console.error("Error fetching dashboard data:", error);
      }
    };

    // Panggil fungsi saat komponen dimuat
    onMounted(() => {
      fetchDashboardData();
    });
    const __returned__ = {
      fetchDashboardData,
      ref,
      onMounted,
      computed,
