import { ref, onMounted, computed } from "vue";
import axios from "axios";
import KepalaGudangLayouts from "@/components/KepalaGudangLayouts.vue";

// State untuk menyimpan data (Mengatasi error: 'stockData', 'stockHistoryData', 'errorMessage' not defined)

export default {
  __name: 'DashboardKepalagudang',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const stockData = ref([]);
    const stockHistoryData = ref([]);
    const errorMessage = ref("");

    // Tanggal hari ini (Mengatasi error: 'today' is not defined)
    // Gunakan tanggal hari ini yang sebenarnya, bukan tanggal hardcode
    const today = new Date().toISOString().split("T")[0];

    // Hitung Total Stock Value dari stockData (Mengatasi error: 'computed' is defined but never used)
    const totalStockValue = computed(() => {
      return stockData.value.reduce((sum, item) => {
        return sum + (item.stock * item.cost_price || 0);
      }, 0);
    });

    // Hitung Items Inbound Today dari stockHistoryData (Mengatasi error: 'computed' is defined but never used)
    const itemsInboundToday = computed(() => {
      return stockHistoryData.value.filter(item => {
        // Perlu diperhatikan: format item.date harus sesuai dengan format 'today'
        return item.date && item.date.startsWith(today) && item.type === "inbound";
      }).reduce((sum, item) => sum + (item.qty || 0), 0);
    });

    // Hitung Items Outbound Today dari stockHistoryData (Mengatasi error: 'computed' is defined but never used)
    const itemsOutboundToday = computed(() => {
      return stockHistoryData.value.filter(item => {
        // Perlu diperhatikan: format item.date harus sesuai dengan format 'today'
        return item.date && item.date.startsWith(today) && item.type === "outbound";
      }).reduce((sum, item) => sum + (item.qty || 0), 0);
    });

    // Fungsi untuk memformat angka ke format mata uang
    const formatCurrency = value => {
      return new Intl.NumberFormat("en-US", {
        style: "currency",
        currency: "USD"
      }).format(value);
    };

    // Fungsi untuk mengambil data dari backend (disesuaikan agar sesuai dengan backend Go Anda)
    const fetchDashboardData = async () => {
      try {
        // 1. Ambil data stok (Menggunakan ?type=stocks)
        const stockResponse = await axios.get("/api/v1/kepala-gudang/reports/stocks", {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("token")}`
          },
          params: {
            type: "stocks" // Wajib agar backend Gin tahu laporan apa yang dicari
          }
        });
        stockData.value = stockResponse.data.data || [];

        // 2. Ambil data riwayat stok (Menggunakan ?type=stock-histories & filter tanggal)
        const historyResponse = await axios.get("/api/v1/kepala-gudang/reports/stock-histories", {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("token")}`
          },
          params: {
            type: "stock-histories",
            // Wajib agar backend Gin tahu laporan apa yang dicari
            from: today,
            to: today
          }
        });
        stockHistoryData.value = historyResponse.data.data || [];

        // Jika berhasil, kosongkan pesan error
        errorMessage.value = "";
      } catch (error) {
        // Tampilkan pesan error jika gagal
        errorMessage.value = "Gagal memuat data dashboard. Silakan coba lagi nanti.";
        console.error("Error fetching dashboard data:", error);

        // Kosongkan data agar tampilan tidak menampilkan angka 0 yang salah
        stockData.value = [];
        stockHistoryData.value = [];
      }
    };

    // Panggil fungsi saat komponen dimuat (Mengatasi error: 'onMounted' is defined but never used)
    onMounted(() => {
      // Pastikan Anda telah mengatur axios.defaults.baseURL ke alamat backend Go Anda
      // Misalnya: axios.defaults.baseURL = 'http://localhost:8080';
      fetchDashboardData();
    });
    const __returned__ = {
      stockData,
      stockHistoryData,
      errorMessage,
      today,
      totalStockValue,
      itemsInboundToday,
      itemsOutboundToday,
      formatCurrency,
      fetchDashboardData,
      ref,
      onMounted,
      computed,
      get axios() {
        return axios;
      },
      KepalaGudangLayouts
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
