import "core-js/modules/es.iterator.constructor.js";
import "core-js/modules/es.iterator.filter.js";
import "core-js/modules/es.iterator.reduce.js";
import { ref, onMounted, computed } from "vue";
import axios from "axios";
import KepalaGudangLayouts from "@/components/KepalaGudangLayouts.vue";

// State untuk menyimpan data

export default {
  __name: 'DashboardKepalagudang',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const stockData = ref([]);
    const stockHistoryData = ref([]);
    const errorMessage = ref("");

    // Tanggal hari ini
    const today = new Date().toISOString().split("T")[0]; // Format YYYY-MM-DD

    // Hitung Total Stock Value dari stockData
    const totalStockValue = computed(() => {
      return stockData.value.reduce((sum, item) => {
        // Asumsi data item memiliki field 'stock' dan 'cost_price'
        return sum + (item.stock * item.cost_price || 0);
      }, 0);
    });

    // Hitung Items Inbound Today
    const itemsInboundToday = computed(() => {
      return stockHistoryData.value.filter(item => {
        // Asumsi data item memiliki field 'date' dan 'type'
        return item.date && item.date.startsWith(today) && item.type === "inbound";
      }).reduce((sum, item) => sum + (item.qty || 0), 0);
    });

    // Hitung Items Outbound Today
    const itemsOutboundToday = computed(() => {
      return stockHistoryData.value.filter(item => {
        return item.date && item.date.startsWith(today) && item.type === "outbound";
      }).reduce((sum, item) => sum + (item.qty || 0), 0);
    });

    // Fungsi untuk memformat angka ke format mata uang
    const formatCurrency = value => {
      return new Intl.NumberFormat("en-US", {
        style: "currency",
        currency: "USD",
        minimumFractionDigits: 0
      }).format(value);
    };

    // Fungsi untuk mengambil data dari backend
    const fetchDashboardData = async () => {
      try {
        const token = localStorage.getItem("token");
        if (!token) {
          errorMessage.value = "Tidak ada token otorisasi. Silakan login.";
          return;
        }
        const headers = {
          Authorization: `Bearer ${token}`
        };

        // 1. Ambil data stok (Menambahkan ?type=stocks)
        const stockResponse = await axios.get("/api/v1/kepala-gudang/reports/stocks", {
          headers,
          params: {
            type: "stocks" // Wajib untuk handler GenerateReport
          }
        });
        stockData.value = stockResponse.data.data || [];

        // 2. Ambil data riwayat stok (Menambahkan ?type=stock-histories & filter tanggal)
        const historyResponse = await axios.get("/api/v1/kepala-gudang/reports/stock-histories", {
          headers,
          params: {
            type: "stock-histories",
            // Wajib untuk handler GenerateReport
            from: today,
            to: today
          }
        });
        stockHistoryData.value = historyResponse.data.data || [];

        // Clear error message jika berhasil
        errorMessage.value = "";
      } catch (error) {
        errorMessage.value = "Gagal memuat data dashboard. Silakan cek koneksi ke backend (http://localhost:8081).";
        console.error("Error fetching dashboard data:", error);
        stockData.value = [];
        stockHistoryData.value = [];
      }
    };

    // Panggil fungsi saat komponen dimuat
    onMounted(() => {
      fetchDashboardData();
    });
    const __returned__ = {
      stockData,
      stockHistoryData,
      errorMessage,
      today,
      totalStockValue,
      itemsInboundToday,
      itemsOutboundToday,
      formatCurrency,
      fetchDashboardData,
      ref,
      onMounted,
      computed,
      get axios() {
        return axios;
      },
      KepalaGudangLayouts
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
