import "core-js/modules/es.iterator.constructor.js";
import "core-js/modules/es.iterator.filter.js";
import "core-js/modules/es.iterator.reduce.js";
import { ref, onMounted, computed } from "vue";
import axios from "axios";
import KepalaGudangLayouts from "@/components/KepalaGudangLayouts.vue";

// State untuk menyimpan data (FIX: Mengatasi error 'not defined')

export default {
  __name: 'DashboardKepalagudang',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const stockData = ref([]);
    const stockHistoryData = ref([]);
    const errorMessage = ref("");

    // Tanggal hari ini (FIX: Mengatasi error 'today' not defined dan menggunakan tanggal real)
    const today = new Date().toISOString().split("T")[0]; // Contoh output: 2025-10-03

    // Hitung Total Stock Value dari stockData
    const totalStockValue = computed(() => {
      return stockData.value.reduce((sum, item) => {
        // Pastikan field di response backend adalah 'stock' dan 'cost_price'
        return sum + (item.stock * item.cost_price || 0);
      }, 0);
    });

    // Hitung Items Inbound Today
    const itemsInboundToday = computed(() => {
      return stockHistoryData.value.filter(item => {
        // Pastikan item.date sesuai format ISO atau hanya tanggal (YYYY-MM-DD)
        return item.date && item.date.startsWith(today) && item.type === "inbound";
      }).reduce((sum, item) => sum + (item.qty || 0), 0);
    });

    // Hitung Items Outbound Today
    const itemsOutboundToday = computed(() => {
      return stockHistoryData.value.filter(item => {
        return item.date && item.date.startsWith(today) && item.type === "outbound";
      }).reduce((sum, item) => sum + (item.qty || 0), 0);
    });

    // Fungsi untuk memformat angka ke format mata uang
    const formatCurrency = value => {
      // Menggunakan USD seperti kode Anda sebelumnya
      return new Intl.NumberFormat("en-US", {
        style: "currency",
        currency: "USD",
        minimumFractionDigits: 0 // Opsional: hilangkan desimal untuk tampilan ringkas
      }).format(value);
    };

    // Fungsi untuk mengambil data dari backend
    const fetchDashboardData = async () => {
      try {
        // 1. Ambil data stok (FIX: Menambahkan parameter 'type' untuk GenerateReport)
        const stockResponse = await axios.get("/api/v1/kepala-gudang/reports/stocks", {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("token")}`
          },
          params: {
            // WAJIB: Sesuai dengan logic GenerateReport di backend
            type: "stocks"
          }
        });
        // Asumsi: Backend mengembalikan data di 'response.data.data'
        stockData.value = stockResponse.data.data || [];

        // 2. Ambil data riwayat stok (FIX: Menambahkan parameter 'type' dan filter tanggal)
        const historyResponse = await axios.get("/api/v1/kepala-gudang/reports/stock-histories", {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("token")}`
          },
          params: {
            // WAJIB: Sesuai dengan logic GenerateReport di backend
            type: "stock-histories",
            from: today,
            to: today
          }
        });
        // Asumsi: Backend mengembalikan data di 'response.data.data'
        stockHistoryData.value = historyResponse.data.data || [];

        // Clear error message jika berhasil
        errorMessage.value = "";
      } catch (error) {
        errorMessage.value = "Gagal memuat data dashboard. Pastikan Backend (Go) berjalan di http://localhost:8081 dan CORS sudah diatur.";
        console.error("Error fetching dashboard data:", error);

        // Reset data jika gagal
        stockData.value = [];
        stockHistoryData.value = [];
      }
    };

    // Panggil fungsi saat komponen dimuat
    onMounted(() => {
      fetchDashboardData();
    });
    const __returned__ = {
      stockData,
      stockHistoryData,
      errorMessage,
      today,
      totalStockValue,
      itemsInboundToday,
      itemsOutboundToday,
      formatCurrency,
      fetchDashboardData,
      ref,
      onMounted,
      computed,
      get axios() {
        return axios;
      },
      KepalaGudangLayouts
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
