              next({ name: "DashboardPembelian" });
              break;
            default:
              next({ name: "DashboardOwner" });
              break;
          }
        } else {
          // Jika tidak ada peran, kembali ke login
          next({ name: "Login" });
        }
      } else {
        // Jika token dan peran valid, lanjutkan
        next();
      }
    } else {
      // Jika tidak ada token, arahkan ke halaman login
      next({ name: "Login" });
    }
  }
  // Jika rute tidak dilindungi (dan bukan login) atau token tidak ada
  else {
    next();
  }
});

export default router;
