import { ref, onMounted, computed } from "vue";
import axios from "axios";
import KepalaGudangLayouts from "@/components/KepalaGudangLayouts.vue";

// State untuk menyimpan data

export default {
  __name: 'DashboardKepalagudang',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const inventoryData = ref([]);
    const errorMessage = ref("");
    const itemsInboundToday = ref(0); // Placeholder, akan diperbarui jika endpoint tersedia
    const itemsOutboundToday = ref(0); // Placeholder, akan diperbarui jika endpoint tersedia

    // Hitung Total Stock Value dari inventoryData
    const totalStockValue = computed(() => {
      return inventoryData.value.reduce((sum, item) => sum + (item.total_value || 0), 0);
    });

    // Fungsi untuk memformat angka ke format mata uang
    const formatCurrency = value => {
      return new Intl.NumberFormat("en-US", {
        style: "currency",
        currency: "USD"
      }).format(value);
    };

    // Fungsi untuk mengambil data dari backend
    const fetchDashboardData = async () => {
      try {
        const response = await axios.get("/api/v1/kepala-gudang/reports/inventory-detail", {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("token")}` // Asumsi token disimpan di localStorage
          }
        });
        inventoryData.value = response.data.data || [];
      } catch (error) {
        errorMessage.value = "Gagal memuat data dashboard. Silakan coba lagi nanti.";
        console.error("Error fetching dashboard data:", error);
      }
    };

    // Panggil fungsi saat komponen dimuat
    onMounted(() => {
      fetchDashboardData();
      // Placeholder untuk itemsInboundToday dan itemsOutboundToday
      // Ini bisa diganti dengan request ke endpoint lain jika tersedia
      itemsInboundToday.value = 350; // Contoh nilai statis, ganti dengan API jika ada
      itemsOutboundToday.value = 280; // Contoh nilai statis, ganti dengan API jika ada
    });
    const __returned__ = {
      inventoryData,
      errorMessage,
      itemsInboundToday,
      itemsOutboundToday,
      totalStockValue,
      formatCurrency,
      fetchDashboardData,
      ref,
      onMounted,
      computed,
      get axios() {
        return axios;
      },
      KepalaGudangLayouts
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
